<script>
	import Select from 'app/components/elementary/Select.svelte';
	import ExternalLink from 'app/components/ExternalLink.svelte';
	import { AGG_DOC_URLS } from 'app/elasticsearch/config';

	export let title;
	export let selectedOption;
	export let hideDisabled;
	export let options;
	export let setAggDocs;

	export let selectionChangedHandler;
</script>

<header class='semibold'>{title}</header>
<Select
	{selectedOption}
	{hideDisabled}
	{options}
	on:selectionChanged={selectionChangedHandler}
	let:option={option}
>
	<div
		class='select-item'
		on:mouseover={() => setAggDocs(option.value)}
		on:mouseout={() => setAggDocs(null)}
	>
		<div>{option.text}</div>
		<ExternalLink  href={AGG_DOC_URLS[option.value]} size={14} />
	</div>
</Select>
