<script>
	import {createEventDispatcher} from 'svelte';
	import {autoID} from 'app/utils/generic';

	const dispatch = createEventDispatcher();

	let id = autoID();
	let value;

	function emitSearchEvent () {
		dispatch('search', value)
	}
</script>


<div>
	<input {id} type='text' bind:value>
	<button on:click={emitSearchEvent}>Search</button>
</div>

<style>
	div {
		margin: .75em;
		display: grid;
		grid-template-columns: 1fr min-content;
	}
	input, button {
		margin: .25em;
		border: 1px solid #0DA8ED;
		border-radius: 3px;
		padding: .5em;
		background: white;
	}

	button { 
		padding: .5em 1em;
		color: #3EBEF6;
		font-weight: bold;
	}
</style>