<script>
	import {createEventDispatcher} from 'svelte';
	import {autoID} from 'utils/generic';

	const dispatch = createEventDispatcher();

	let id = autoID();
	let value;

	function emitSearchEvent () {
		dispatch('search', value)
	}

	function emitEditEvent (newValue) {
		dispatch('edit', newValue);
	}

	$: emitEditEvent(value);
</script>


<div>
	<input {id} type='text' bind:value on:change={emitSearchEvent}>
	<button class='clickable' on:click={emitSearchEvent}>Search</button>
</div>

<style>
	div {
		display: grid;
		grid-template-columns: 1fr min-content;
		margin: .75em;
	}

	input, button {
		background: white;
		border-radius: 3px;
		border: 1px solid #0DA8ED;
		font-size: medium;
		margin: .25em;
		padding: .5em;
	}

	button {
		color: #3EBEF6;
		font-weight: bold;
