<script>
	import {createEventDispatcher} from 'svelte';
	import {autoID} from 'utils/generic';

	const dispatch = createEventDispatcher();

	let id = autoID();
	let value;
	let textInput;
	let searchButton;

	export let fieldName = '';

	export function focus () {
		textInput.focus();
	}

	function handleKeyDown (event) {
		switch (event.keyCode) {
			case 40:
				dispatch('downArrow');
				event.preventDefault();
				break;
			case 38:
				dispatch('upArrow');
				event.preventDefault();
				break;
			default:
		}
	}

	function emitFocusEvent () {
		dispatch('focus');
	}

	function emitBlurEvent () {
		dispatch('blur');
	}

	function emitSearchEvent () {
		dispatch('search', value);
		searchButton.focus();
	}

	function emitEditEvent (newValue) {
		dispatch('edit', newValue);
	}

	$: emitEditEvent(value);
</script>

<div>
	<span>
		<input
			bind:this={textInput}
			{id}
			type='text' 
			bind:value 
			on:change={emitSearchEvent}
			on:keydown={handleKeyDown}
			on:focus={emitFocusEvent}
			on:blur={emitBlurEvent}
			autocomplete='off'
		>
		<label for={id}>{fieldName}</label>
	</span>
	<button
		bind:this={searchButton}
		class='clickable'
		on:click={emitSearchEvent}
	>Search</button>
</div>

