<script>
export let options;
export let selectedOption;
export let unselectable = true;
</script>

<select bind:value={selectedOption} size='100'>
    {#if unselectable}
        <option value='none'>None</option>
    {/if}
    {#each options as opt}
        <option value={opt.value} disabled={opt.disabled}>{opt.text}</option>
    {/each}
</select>

<style>
select {
    grid-area: select; 
    overflow: auto;
}
option {
    font-weight: bold;
}

option:disabled {
    color: red;
    font-weight: normal;
}

</style>