<script>
    export let id;
    export let required;
    export let value;
</script>

<div class:required class:unset={value === 'null'}>
	<input id={`${id}-unset`} type='radio' bind:group={value} value={'null'}>
	<label for={`${id}-unset`} class='boolean-state'>Unset</label>
	<input id={`${id}-true`} type='radio' bind:group={value} value={true}>
	<label for={`${id}-true`} class='boolean-state'>True</label>
	<input id={`${id}-false`} type='radio' bind:group={value} value={false}>
	<label for={`${id}-false`} class='boolean-state'>False</label>
</div>

<style>
    .required.unset {
        color: red;
    }
</style>
