<script>
	import { createEventDispatcher } from 'svelte';

	import * as _ from 'lamb';
	import { isString, sliceStringAt } from '@svizzle/utils';
	import { autoID, isKeyOf, getKeyOf } from 'app/utils/generic';
	import {
		equalsJson,
		extractBaseType,
		hasRecord,
		hasUnits,
		isArrayObject,
		isArrayString,
		isStringLiteralUnion
	} from 'app/elasticsearch/utils/aggParams';

	import IconHelpCircle from 'app/components/icons/IconHelpCircle.svelte';

	import ArrayEditor from './ArrayEditor.svelte';
	import BooleanRadios from './BooleanRadios.svelte';
	import NumericalUnitsEditor
		from 'app/components/elementary/NumericalUnitsEditor.svelte';
	import EsGeoPointObj from './EsGeoPointObj.svelte';
	import ExistenceEditor from './ExistenceEditor.svelte';
	import MinMax from './MinMax.svelte';
	import ObjectEditor from './ObjectEditor.svelte';
	import RadioList from './RadioList.svelte';
	import SimpleField from './SimpleField.svelte';

	const dispatch = createEventDispatcher();

	const id = autoID();

	const typeEditors = {
		'boolean': {
			component: BooleanRadios
		},
		'integer': {
			component: SimpleField,
			props: {
				type: 'number',
				dataType: 'integer'
			}
		},
		'float': {
			component: SimpleField,
			props: {
				type: 'number',
				dataType: 'float'
			}
		},
		'ES_keyword': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'ES_keyword'
			}
		},
		'ES_text': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'ES_text'
			}
		},
		'ES_text_w_keyword': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'ES_text_w_keyword'
			}
		},
		'string': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'string'
			}
		},
		'date': {
			component: SimpleField,
			props: {type: 'date'}
		},
		'Date_YYYYMMDD_dash': {
			component: SimpleField,
			props: {
				type: 'date',
				dataType: 'Date_YYYYMMDD_dash'
			}
		},
		'Date_YYYYMMDD_dash_time': {
			component: SimpleField,
			props: {
				type: 'date',
				dataType: 'Date_YYYYMMDD_dash'
			}
		},
		'MinMax<integer>': {
			component: MinMax,
			props: {
				dataType: 'integer'
			}
		},
		'MinMax<float>': {
			component: MinMax,
			props: {
				dataType: 'float'
			}
		},
		'ES_geo_point_obj': {
			component: EsGeoPointObj,
			props: {
				type: 'number',
				dataType: 'float'
			}
		},
		'Existence': {
			component: ExistenceEditor,
			props: {
				dataType: 'Existence'
			}
		},
	}

	export let dataType;
	export let defaultValue;
	export let help = true;
	export let labelText = '';
	export let required;
	export let typeObject;
	export let value = null;

	let editor;

	function displayDocs () {
		dispatch('docs', 'display');
	}

	function hideDocs () {
		dispatch('docs', 'hide');
	}

	function setDocs () {
		dispatch('docs', 'set');
	}

	function unsetDocs () {
		dispatch('docs', 'unset');
	}

	function handleChange (event) {
		dispatch('change', event.detail);
	}

	const makeConfigEditor = component => props => ({component, props});
	const unionEditor = makeConfigEditor(RadioList);
	const arrayEditor = makeConfigEditor(ArrayEditor);
	const numUnitsEditor = makeConfigEditor(NumericalUnitsEditor);
	const simpleEditor = makeConfigEditor(SimpleField);
	const objectEditor = makeConfigEditor(ObjectEditor);

	const configUnionEditor = type => unionEditor({
		typeObject: type
	});
	const configArrayEditor = type => arrayEditor({
		// eslint-disable-next-line no-use-before-define
		fieldEditor: getEditor(type)
	});
	const configArrayStringEditor = _.pipe([
		sliceStringAt([0, -2]),
		configArrayEditor
	]);
	const configArrayObjectEditor = _.pipe([
		_.getKey('__array'),
		configArrayEditor
	]);
	const configNumUnitsEditor = ({__units}) => numUnitsEditor({
		unitList: __units
	});
	const configJsonEditor = type => simpleEditor({
		type: 'json',
		typeObject: type
	});
	const configObjectEditor = type => objectEditor({
		typeObject: type
	});

	const getEditor = _.pipe([
		extractBaseType,
		_.adapter([
			_.casus(
				_.allOf([isString, isKeyOf(typeEditors)]),
				getKeyOf(typeEditors)
			),
			_.casus(isStringLiteralUnion, configUnionEditor),
			_.casus(isArrayObject, configArrayObjectEditor),
			_.casus(isArrayString, configArrayStringEditor),
			_.casus(hasUnits, configNumUnitsEditor),
			_.casus(_.anyOf([equalsJson, hasRecord]), configJsonEditor),
			configObjectEditor
		])
	]);

	$: editor = getEditor(typeObject);
</script>

<label
	for={id}
	on:mouseover={displayDocs}
	on:mouseout={hideDocs}
	title={dataType}
	class:inline={!help && (typeObject && isStringLiteralUnion(typeObject) || dataType === 'boolean')}
>
	{labelText}
	{#if help}
		<IconHelpCircle size={14} />
	{/if}
</label>
<div
	on:mouseover={displayDocs}
	on:mouseout={hideDocs}
>
	{#if editor}
		<svelte:component
			this={editor.component}
			{value}
			{id}
			{defaultValue}
			{required}
			on:change={handleChange}
			on:focus={setDocs}
			on:blur={unsetDocs}
			{...editor.props}
		/>
	{/if}
</div>

<style>
	label {
		white-space: nowrap;
		justify-self: end;
	}
	.inline {
		float: left;
		margin-right: 1em;
		font-weight: bold;
		width: 25%;
		min-width: min-content;
	}
</style>
