<script>
	import { createEventDispatcher } from 'svelte';
	import IconHelpCircle from 'app/components/icons/IconHelpCircle.svelte';

	import { autoID } from 'app/utils/generic';
	import { extractBaseType } from 'app/elasticsearch';

	import SimpleField from './SimpleField.svelte';
	// import DateTimeField from './SimpleField.svelte';
	import BooleanRadios from './BooleanRadios.svelte';
	import MinMax from './MinMax.svelte';
	import EsGeoPointObj from './EsGeoPointObj.svelte';
	import ObjectEditor from './ObjectEditor.svelte';
	import RadioList from './RadioList.svelte';
	import DateInterval from './DateInterval.svelte';
	import ExistenceEditor from './ExistenceEditor.svelte';
	import ArrayEditor from './ArrayEditor.svelte';

	const dispatch = createEventDispatcher();

	const id = autoID();

	const typeEditors = {
		'boolean': {
			component: BooleanRadios
		},
		'integer': {
			component: SimpleField,
			props: {
				type: 'number',
				dataType: 'integer'
			}
		},
		'float': {
			component: SimpleField,
			props: {
				type: 'number',
				dataType: 'float'
			}
		},
		'ES_keyword': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'ES_keyword'
			}
		},
		'ES_text': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'ES_text'
			}
		},
		'ES_text_w_keyword': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'ES_text_w_keyword'
			}
		},
		'string': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'string'
			}
		},
		'date': {
			component: SimpleField,
			props: {type: 'date'}
		},
		'Date_YYYYMMDD_dash': {
			component: SimpleField,
			props: {
				type: 'date',
				dataType: 'Date_YYYYMMDD_dash'
			}
		},
		'Date_YYYYMMDD_dash_time': {
			component: SimpleField,
			props: {
				type: 'date',
				dataType: 'Date_YYYYMMDD_dash'
			}
		},
		'MinMax<integer>': {
			component: MinMax,
			props: {
				dataType: 'integer'
			}
		},
		'MinMax<float>': {
			component: MinMax,
			props: {
				dataType: 'float'
			}
		},
		'ES_geo_point_obj': {
			component: EsGeoPointObj,
			props: {
				type: 'number',
				dataType: 'float'
			}
		},
		'DateInterval': {
			component: DateInterval,
			props: {
				dataType: 'ms, s, m, h, d, w, M, q, y'
			}
		},
		'CalendarInterval': {
			component: DateInterval,
			props: {
				dataType: 'm, h, d, w, M, w, q, y'
			}
		},
		'FixedInterval': {
			component: DateInterval,
			props: {
				dataType: 'ms, s, m, h, d'
			}
		},
		'Existence': {
			component: ExistenceEditor,
			props: {
				dataType: 'Existence'
			}
		},
	}

	export let dataType;
	export let typeObject;
	export let defaultValue;
	export let help = true;
	export let labelText = '';
	export let required = false;
	export let value = null;

	let editor;

	function displayDocs () {
		dispatch('docs', 'display');
	}

	function hideDocs () {
		dispatch('docs', 'hide');
	}

	function setDocs () {
		dispatch('docs', 'set');
	}

	function unsetDocs () {
		dispatch('docs', 'unset');
	}

	function handleChange (event) {
		dispatch('change', event.detail);
	}

	function isStringUnion (type) {
		return Array.isArray(type) && type.every(subType => {
			return subType === 'undefined'
				|| typeof subType === 'string' && subType.startsWith('"');
		});
	}

	function getEditor (targetDataType, targetTypeObject) {
		let simplfiedTypeObject = extractBaseType(targetTypeObject);
		console.log("getting Editor", targetDataType, simplfiedTypeObject)
		if (targetDataType in typeEditors) {
			return typeEditors[targetDataType]
		} else if (simplfiedTypeObject && isStringUnion(simplfiedTypeObject)) {
			const unionEditor = {
				component: RadioList,
				props: {
					dataType: targetDataType,
					list: targetDataType.split('|').map(i => i.trim().replaceAll('"',''))
				}
			};
			return unionEditor;
		} else if (targetDataType.endsWith('[]')) {
			let subType;
			if (typeof simplfiedTypeObject === 'string' && simplfiedTypeObject.endsWith('[]')) {
				subType = simplfiedTypeObject.substr(0, simplfiedTypeObject.length - 2);
			} else if (simplfiedTypeObject && '__array' in simplfiedTypeObject) {
				subType = simplfiedTypeObject.__array;
			} else {
				throw new Error("Failed to parse array.");
			}
			const subDataType = targetDataType.substr(0, targetDataType.length - 2);
			const arrayEditor = {
				component: ArrayEditor,
				props: {
					fieldEditor: getEditor(subDataType, subType)
				}
			}
			return arrayEditor;
		} else if (targetDataType === 'json' || '__record' in simplfiedTypeObject) {
			return {
				component: SimpleField,
				props: {
					type: 'json',
					dataType: targetDataType
				}
			}
		}
		return  {
			component: ObjectEditor,
			props: {
				type: 'json',
				dataType: targetDataType,
				typeObject: simplfiedTypeObject
			}
		};
	}

	$: editor = getEditor(dataType, typeObject);
</script>

<label
	for={id}
	on:mouseover={displayDocs}
	on:mouseout={hideDocs}
	title={dataType}
	class:inline={!help && (typeObject && isStringUnion(typeObject) || dataType === 'boolean')}
>
	{labelText}
	{#if help}
		<IconHelpCircle size={14} />
	{/if}
</label>
<div
	on:mouseover={displayDocs}
	on:mouseout={hideDocs}
>
	{#if editor}
		<svelte:component
			this={editor.component}
			{value}
			{id}
			{defaultValue}
			{required}
			on:change={handleChange}
			on:focus={setDocs}
			on:blur={unsetDocs}
			{...editor.props}
		/>
	{/if}
</div>

<style>
	label {
		white-space: nowrap;
		justify-self: end;
	}
	.inline {
		float: left;
		margin-right: 1em;
		font-weight: bold;
		width: 25%;
		min-width: min-content;
	}
</style>
