<script>
	// eslint-disable-next-line node/no-unpublished-import
	import { createEventDispatcher } from 'svelte';
	import SimpleField from './SimpleField.svelte';
	
	const dispatch = createEventDispatcher();

	export let id = null;
	export let dataType = 'null';
	export let required = false;
	export let value = null;
	export let defaultValue;
	
	let type;
	let lat;
	let lon;
	let defaultLat;
	let defaultLon;
	let notEmpty = false;
	let complete = false;

	function handleValueChange (newValue) {
		if (newValue) {
			lat = newValue.lat;
			lon = newValue.lon;
			notEmpty = Boolean(lat || lon);
			complete = Boolean(lat && lon);
		} else {
			lat = null;
			lon = null;
			notEmpty = false;
			complete = false;		
		}
		console.log('value changed', lat, lon, notEmpty, complete);
	}
	function handleEditorChange (newValue) {
		let payload = null;
		if (complete) {
			payload = newValue;
		}
		dispatch('change', payload);
	}
	function handleChangeLat (event) {
		const newValue = {lat: event.detail, lon};
		handleValueChange(newValue);
		handleEditorChange(newValue);
	}
	function handleChangeLon (event) {
		const newValue = {lat, lon: event.detail};
		handleValueChange(newValue);
		handleEditorChange(newValue);
	}

	function handleFocus () {
		dispatch('focus');
	}
	function handleBlur () {
		dispatch('blur');
	}

	const types = {
		'integer': 'number',
		'float': 'number',
		'string': 'text',
	};
	
	$: type = types[dataType];
	$: handleValueChange(value);
	$: defaultValue && (
		defaultLat = defaultValue.lat,
		defaultLon = defaultValue.lon
	) || !defaultValue && (
		defaultLat = undefined,
		defaultLon = undefined
	);
</script>

<label for={id + '-lat'}>Lat</label>
<SimpleField
	id={id + '-min'}
	{type} 
	{dataType}
	value={lat}
	defaultValue={defaultLat}
	on:change={handleChangeLat}
	on:focus={handleFocus}
	on:blur={handleBlur}
	required={required || notEmpty}
/>
<label for={id + '-max'}>Lon</label>
<SimpleField
	id={id + '-lon'}
	{type} 
	{dataType}
	value={lon}
	defaultValue={defaultLon}
	on:change={handleChangeLon}
	on:focus={handleFocus}
	on:blur={handleBlur}
	required={required || notEmpty}
/>