<script>
	import {createEventDispatcher} from 'svelte';

	import FieldCount from './FieldCount.svelte';

	const dispatch = createEventDispatcher();

	export let fieldStats = [];
	export let selectedFieldName;

	function selectField (fieldName) {
		selectedFieldName = fieldName;
		dispatch('fieldSelected', fieldName);
	}
</script>

{#each fieldStats as field}
	<div
		class='fields'
		on:mousedown={() => selectField(field.name)}
		class:selected={field.name === selectedFieldName}
	>
		<FieldCount
			fieldName={field.name}
			count={field.count}
		/>
	</div>
{/each}

<style>
	div {
		background: white;
		font-size: medium;
		padding: .5em;
		color: #52C5FF;
		border-bottom: thin solid #B4E4FF;
	}

	div:last-child {
		border-bottom: none;
	}
	div.selected {
		background: #DAF1FF;
		color: #48C1FF;
		font-family: 'Open Sans Bold';
		font-weight: bold;
	}
	div.selected * {
		font-family: 'Open Sans Bold';
		font-weight: bold;
	}
</style>
