<script>
	// eslint-disable-next-line node/no-unpublished-import
	import { createEventDispatcher } from 'svelte';

	import PanelMenu from 'app/components/elementary/PanelMenu.svelte';
	import MenuItem from 'app/components/elementary/MenuItem.svelte';
	import { autoID } from 'app/utils/generic';

	const dispatch = createEventDispatcher();

	let hideDisabledID = autoID();
	export let hideDisabled = false;

	$: dispatch('hideDisabledChanged', hideDisabled);
</script>

<PanelMenu>
	<MenuItem>
		<input
			bind:checked={hideDisabled}
			id={hideDisabledID}
			type='checkbox'
		/>
		<label
			for={hideDisabledID}
			class='clickable'
		>Hide disabled items</label>
	</MenuItem>
</PanelMenu>
