<script>
	import { createEventDispatcher } from 'svelte';

	import * as _ from 'lamb';
	import { autoID, getKeyOf } from 'app/utils/generic';
	import {
		getArrayCellType,
		is_array,
		is_enums,
		is_native,
		is_object,
		is_xor,
	} from 'app/elasticsearch/types/genericTypes'

	import IconHelpCircle from 'app/components/icons/IconHelpCircle.svelte';

	import ArrayEditor from './ArrayEditor.svelte';
	import BooleanRadios from './BooleanRadios.svelte';
	import NumericalUnitsEditor
		from 'app/components/elementary/NumericalUnitsEditor.svelte';
	import EsGeoPointObj from './EsGeoPointObj.svelte';
	import ExistenceEditor from './ExistenceEditor.svelte';
	import MinMax from './MinMax.svelte';
	import ObjectEditor from './ObjectEditor.svelte';
	import RadioList from './RadioList.svelte';
	import SimpleField from './SimpleField.svelte';

	const dispatch = createEventDispatcher();

	const id = autoID();

	const typeEditors = {
		'boolean': {
			component: BooleanRadios
		},
		'integer': {
			component: SimpleField,
			props: {
				type: 'number',
				dataType: 'integer'
			}
		},
		'float': {
			component: SimpleField,
			props: {
				type: 'number',
				dataType: 'float'
			}
		},
		'ES_keyword': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'ES_keyword'
			}
		},
		'ES_text': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'ES_text'
			}
		},
		'ES_text_w_keyword': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'ES_text_w_keyword'
			}
		},
		'string': {
			component: SimpleField,
			props: {
				type: 'text',
				dataType: 'string'
			}
		},
		'date': {
			component: SimpleField,
			props: {type: 'date'}
		},
		'Date_YYYYMMDD_dash': {
			component: SimpleField,
			props: {
				type: 'date',
				dataType: 'Date_YYYYMMDD_dash'
			}
		},
		'Date_YYYYMMDD_dash_time': {
			component: SimpleField,
			props: {
				type: 'date',
				dataType: 'Date_YYYYMMDD_dash'
			}
		},
		'MinMax<integer>': {
			component: MinMax,
			props: {
				dataType: 'integer'
			}
		},
		'MinMax<float>': {
			component: MinMax,
			props: {
				dataType: 'float'
			}
		},
		'ES_geo_point_obj': {
			component: EsGeoPointObj,
			props: {
				type: 'number',
				dataType: 'float'
			}
		},
		'Existence': {
			component: ExistenceEditor,
			props: {
				dataType: 'Existence'
			}
		},
	}

	export let dataType;
	export let help = true;
	export let labelText = '';
	export let required;
	export let typeObject;
	export let value = null;
	
	let editor;
	let defaultValue;

	function displayDocs () {
		dispatch('docs', 'display');
	}

	function hideDocs () {
		dispatch('docs', 'hide');
	}

	function setDocs () {
		dispatch('docs', 'set');
	}

	function unsetDocs () {
		dispatch('docs', 'unset');
	}

	function handleChange (event) {
		dispatch('change', event.detail);
	}

	const extractBaseType = _.getKey('__type');

	const makeEditor = component => props => ({component, props});
	const unionEditor = makeEditor(RadioList);
	const arrayEditor = makeEditor(ArrayEditor);
	const numUnitsEditor = makeEditor(NumericalUnitsEditor);
	const simpleEditor = makeEditor(SimpleField);
	const objectEditor = makeEditor(ObjectEditor);

	const configNativeEditor = _.pipe([
		extractBaseType,
		getKeyOf(typeEditors)
	])
	const configEnumsEditor = type => unionEditor({
		typeObject: type.values
	});
	const configArrayEditor = type => arrayEditor({
		// eslint-disable-next-line no-use-before-define
		fieldEditor: getEditor(type)
	});
	const configArrayObjectEditor = _.pipe([
		getArrayCellType,
		configArrayEditor
	]);
	const configNumUnitsEditor = ({__units}) => numUnitsEditor({
		unitList: __units
	});
	const configObjectEditor = type => objectEditor({
		typeObject: type
	});
	const configJsonEditor = type => simpleEditor({
		type: 'json',
		typeObject: type
	});

	const getEditor = _.adapter([
		_.casus(is_native, configNativeEditor),
		_.casus(is_enums, configEnumsEditor),
		_.casus(is_array, configArrayObjectEditor),
		_.casus(_.anyOf([is_object, is_xor]), configObjectEditor),
		// _.casus(hasUnits, configNumUnitsEditor),
		//_.casus(_.anyOf([equalsJson, hasRecord]), configJsonEditor),
		configJsonEditor
	]);

	$: {
		editor = getEditor(typeObject);
		defaultValue = typeObject.__default;
	}
</script>

<label
	for={id}
	on:mouseover={displayDocs}
	on:mouseout={hideDocs}
	title={dataType}
	class:inline={!help && (typeObject /* && isStringLiteralUnion(typeObject) */ || dataType === 'boolean')}
>
	{labelText}
	{#if help}
		<IconHelpCircle size={14} />
	{/if}
</label>
<div
	on:mouseover={displayDocs}
	on:mouseout={hideDocs}
>
	{#if editor}
		<svelte:component
			this={editor.component}
			{value}
			{id}
			{defaultValue}
			{required}
			on:change={handleChange}
			on:focus={setDocs}
			on:blur={unsetDocs}
			{...editor.props}
		/>
	{/if}
</div>

<style>
	label {
		white-space: nowrap;
		justify-self: end;
	}
	.inline {
		float: left;
		margin-right: 1em;
		font-weight: bold;
		width: 25%;
		min-width: min-content;
	}
</style>
