export type Opaque<T, K> = T & { __opaque__: K };

export type date = Opaque<Date, 'date'>;
export type float = Opaque<number, 'float'>;
export type integer = Opaque<number, 'integer'>;

export type Date_YYYYMMDD = Opaque<string, 'Date_YYYYMMDD'>;
export type Date_YYYYMMDD_dash = Opaque<string, 'Date_YYYYMMDD_dash'>;
export type Date_YYYYMMDD_dash_time = Opaque<string, 'Date_YYYYMMDD_dash_time'>;
export type Date_YYYYMMDD_dash_T_time = Opaque<string, 'Date_YYYYMMDD_dash_T_time'>;

export type ES_geo_point_obj = {
    lat: float,
    lon: float
}

export type ES_keyword = Opaque<string, 'ES_keyword'>;
export type ES_text = Opaque<string, 'ES_text'>;
export type ES_text_array = Opaque<string, 'ES_text_array'>;
export type ES_text_w_keyword  = Opaque<string, 'ES_text_w_keyword'>;
