// eslint-disable-next-line node/no-unpublished-import
import { assign } from 'xstate';

function setTab (ctx, event) {
	ctx.selectedRequestTab.set(event.selectedRequestTab);
	console.log(event);
	return ctx
}

export const configurationOptions = {
	actions: {
		/**
		 * Toggles auto-executing requests when the query is valid
		 */
		toggleAutoExecute: ctx => ctx.runQueryOnSelect.update(v => !v),
		/**
		 * Hide unselectable axes. Default: true
		 */
		toggleHideDisabledForms: ctx => ctx.hideDisabledForms.update(v => !v),
		/**
		 * Hide unselectable aggregations. Default: false
		 */
		toggleHideDisabledAggs: ctx => ctx.hideDisabledAggregations.update(v => !v),
		/**
		 * Hide unselectable datasets. Default: false
		 */
		toggleHideDisabledDatasets: ctx => ctx.hideDisabledDatasets.update(v => !v),
		/**
		 * Hide unselectable fields. Default: true
		 */
		toggleHideDisabledFields: ctx => ctx.hideDisabledFields.update(v => !v),
		/**
		 * Show full response or only aggregation results.
		 */
		toggleShowFullResponse: ctx => ctx.showFullResponse.update(v => !v),
		setTab: assign(setTab),

	},
	guards: {
	}
};
