import { configurationConfig } from './formconfig.config';
// import { formConfig } from './form.config';
import { docsConfig } from './docs.config';

export const routeConfig = {
	id: 'Route',
	initial: 'Loading',
	on: {
		ROUTE_CHANGED: {
			target: 'Interactive',
			actions: [
				'resetForms',
				'parseParams'
			]
		}
	},
	states: {
		Loading: {
			on: {
				READY: {
					target: 'Interactive',
					actions: [
						'parseParams'
					]
				}
			}
		},
		Interactive: {
			onEntry: ['selectForm'],
			type: 'parallel',
			states: {
				FormConfig: configurationConfig,
				Docs: docsConfig,
			},
			on: {
				FORM_ACTIVATED: {
					actions: [
						'log',
						'selectForm'
					]
				}
			}
		}
	}
};
