// eslint-disable-next-line node/no-unpublished-import
import { createMachina } from '../../utils';

import { configurationConfig } from './formconfig.config';
import { configurationOptions } from './formconfig.options';
import { selectionConfig } from './form.config';
import { selectionOptions } from './form.options';
import { docsConfig } from './docs.config';
import { docsOptions } from './docs.options';

import { createBuilderStores } from './context';

export const routeConfig = {
	id: 'Route',
	initial: 'Loading',
	on: {
		ROUTE_CHANGED: {
			target: 'Navigating'
		}
	},
	states: {
		Loading: {
			on: {
				READY: {
					target: 'Interactive',
					actions: ['parseParams']
				}
			}
		},
		Navigating: {
			on: {
				NAVIGATED: {
					target: 'Interactive',
					actions: ['parseParams']
				}
			}
		},
		Interactive: {
			type: 'parallel',
			states: {
				FormConfig: configurationConfig,
				Docs: docsConfig,
				Form: selectionConfig,
			}
		}
	}
};

export const routeOptions = {
	actions: {
		...configurationOptions.actions,
		...selectionOptions.actions,
		...docsOptions.actions,
	},
	guards: {
		...configurationOptions.guards,
		...selectionOptions.guards,
		...docsOptions.guards,
	}
};

export const createBuilderMachine = () => createMachina(routeConfig, routeOptions, createBuilderStores());
