import { createMachina } from '../utils';

import { routeConfig } from './route.config';
import { selectionEditingOptions } from './selectionEditing.options';
import { createSelectionStores } from './selectionEditing.context';

export const routeOptions = {
	actions: {
		...selectionEditingOptions.actions,
	},
	guards: {
		...selectionEditingOptions.guards,
	}
};

export const createExploreMachine = () => createMachina(
	routeConfig,
	routeOptions,
	createSelectionStores()
);
