import {writable} from 'svelte/store';

export const createSearchStores = () => {
	const activeFieldName = writable(null);
	const userSelection = writable(null);
	const searchQuery = writable('');

	const isFieldsMenuActive = writable(false);
	const termCountsPerFieldId = writable([]);
	const suggetionsByFieldId = writable({});
	const response = writable(null);

	return {
		activeFieldName,
		isFieldsMenuActive,
		response,
		searchQuery,
		suggetionsByFieldId,
		termCountsPerFieldId,
		userSelection,
	}
};
