import {writable} from 'svelte/store';

export const createSearchStores = () => {
	const selectedFieldName = writable();
	const userSelection = writable();
	const searchQuery = writable('');

	const isFieldsMenuActive = writable(false);

	const fieldStats = writable([]);
	const suggestions = writable([]);

	const statsCache = writable({});
	const statsCacheKey = writable();

	const suggestionsCache = writable({});
	const suggestionsCacheKey = writable();

	return {
		selectedFieldName,
		isFieldsMenuActive,
		searchQuery,
		suggestions,
		fieldStats,
		userSelection,
		statsCache,
		statsCacheKey,
		suggestionsCache,
		suggestionsCacheKey,
	}
};
